import { React, useState } from "react";
import { Container, Row, Col } from 'react-bootstrap';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import { motion } from 'framer-motion';

export default function Signup() {
    const [name, setname] = useState("");
    const [email, setemail] = useState("");
    const [password, setpassword] = useState("");

    const navi = useNavigate();

    const gotosignin = () => {
        navi('/');
    }

    const submitdata = (e) => {
        e.preventDefault();

        if (!name) {
            alert("Name Cannot be Empty");
        }
        else if (!email) {
            alert("Email Cannot be Empty");
        }
        else if (!password) {
            alert("Password Cannot be Empty");
        }
        else {
            try {
                axios.post('http://localhost:3001/admin/signup',
                    {
                        name: name,
                        email: email,
                        password: password
                    })
                    .then((res) => {
                        alert(res.data.msg);
                        navi('/');
                    });
            } catch (error) {
                alert("Something Went Wrong", error);
            }
        }
    }

    return (
        <>
            <Container className="text-center heading">
                <Row>
                    <Col>
                        <h1 className="animated fadeIn">Sign Up for Fee Management</h1>
                        <p className="mt-2">Join our fee management system to keep track of your finances easily.</p>
                    </Col>
                </Row>
            </Container>

            <Container>
                <Row>
                    <Col className="bg-light p-4 shadow-lg rounded-lg" sm={12} md={6}>
                        <motion.div
                            className="welcome-message"
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            transition={{ duration: 1 }}
                        >
                            <h1>Welcome to Fee Management System</h1>
                            <h5>Already have an account?</h5>
                            <button
                                className="btn btn-success m-3"
                                onClick={gotosignin}
                            >
                                Sign In
                            </button>
                        </motion.div>
                    </Col>

                    <Col sm={12} md={6}>
                        <motion.div
                            className="signup-form-container"
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            transition={{ duration: 1 }}
                        >
                            <form onSubmit={submitdata}>
                                <h1 className="text-center m-4 p-2 text-white bg-primary rounded">
                                    Sign Up
                                </h1>

                                <div className="form-group my-3">
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Name"
                                        onChange={(e) => setname(e.target.value)}
                                    />
                                </div>

                                <div className="form-group my-3">
                                    <input
                                        type="email"
                                        className="form-control"
                                        placeholder="Email"
                                        onChange={(e) => setemail(e.target.value)}
                                    />
                                </div>

                                <div className="form-group my-3">
                                    <input
                                        type="password"
                                        className="form-control"
                                        placeholder="Password"
                                        onChange={(e) => setpassword(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <input
                                        type="submit"
                                        value="Submit"
                                        className="btn btn-primary btn-block my-3"
                                    />
                                </div>
                            </form>
                        </motion.div>
                    </Col>
                </Row>
            </Container>
        </>
    );
}
