import React, { useState } from "react";

import {Container, Row,Col} from 'react-bootstrap'

import { useNavigate } from "react-router-dom";

import axios from "axios";

export default function Login()
{

    const[email, setemail] = useState("");
    const[password, setpassword] = useState("");

    const navi = useNavigate();

    const gotosignuo = () =>
    {
        navi('/signup')
    }

    const submitdata = (e) =>
    {
        e.preventDefault();


        if((!email) )
            {
                 alert("Email Cannnot be Empty")
            }

            else  if((!password) )
                {
                     alert("Password Cannnot be Empty")
                }

        else
        {
             try {

                axios.post('http://localhost:3001/admin/signin', 
                    {
                        email:email,
                        password:password
                    }
                 )

                 .then((res) =>
                {
                    alert("Login Successful");
                    localStorage.setItem("name", res.data.msg.name)
                    navi('/home')

                })

             } catch (error) {

                alert(error)
             }
        }


    }


    return(
        <>

           <Container className="text-center heading">
                <Row>
                    <Col> <h1> Admin Login</h1> </Col>
                </Row>
           </Container>


           <Container>
                       <Row>

                        <Col className="bg-primary loginLeft" sm={12} md={6}>

                         <h1>Welcome to login</h1>
                         <h5> dont have account ?</h5>
                         <button className="btn btn-warning m-3" onClick={gotosignuo}>SignUp</button>

                        </Col>

                        <Col sm={12} md={6}>

                        <div className="mt-5">


                        <form onSubmit={submitdata}>
                        <h1 className="text-center m-5 bg-primary p-2 text-white" >SignIn</h1>

                            <input type="email" placeholder="Email"  onChange={(e) => setemail(e.target.value)}/>
                            <input type="password" placeholder="Password" onChange={(e) => setpassword(e.target.value)}/>

                            <input type="submit" value="SignIn" className="mb-5" />

                        </form>
                        </div>

                         </Col>
                        </Row>


           </Container>



        </>
    )
}
