import { useEffect, useState } from 'react';
import {Container,Row,Col} from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function Home ()
{

  let navi = new useNavigate();
  let[uname, setuname]= useState('')

  let[student, setstudent]= useState(0)
  let[total, settotal]= useState(0)
  let[received, setreceived]= useState(0)
  let[pending, setpending]= useState(0)
  let[data, setdata]= useState(0)


   useEffect(() =>
  {

       let name = localStorage.getItem("name");

        setuname(name)
       if(!name)
       {
           navi('/');
       }

  },[])


  useEffect(()=>
    {
        axios.post('http://localhost:3001/student/view', 
            {

            }
         )
         .then((res)=>
        {
           setdata(res.data.message);


            setstudent((res.data.message).length)


            let fees = 0;  
            for (let i = 0; i < res.data.message.length; i++) {
                fees += res.data.message[i].fees;   
            }
            settotal(fees);  


            let Rfees = 0;  
            for (let i = 0; i < res.data.message.length; i++) {
              Rfees += res.data.message[i].received;   
            }
            setreceived(Rfees);  

            let Pfees = 0;  
            for (let i = 0; i < res.data.message.length; i++) {
              Pfees += res.data.message[i].pending;   
            }
            setpending(Pfees);  








        })
    },[])


  let logout = () =>
  {
       localStorage.removeItem("name");
       navi('/')
  }

  let gotostudent = () =>
    {

         navi('/student')
    }

    return(
        <Container fluid className='p-5'>

            <Row>
                <Col className='text-end p-4'>

                <h1 >Welcome back {uname} <a onClick={logout} className='bg-danger p-2 text-white '>logout</a></h1>



                </Col>
            </Row>

            <Row>
                <Col sm={12} md={12} className='bg-primary text-white text-center p-5 '>
                  <h3>No Of Students</h3>
                  <h1> {student}</h1>
                  <button onClick={gotostudent} className='btn btn-success'>View Student Lists</button>
                </Col>

                <Col sm={12} md={4} className='bg-info text-white text-center p-5 '>
                  <h3>Total Fees</h3>
                  <h1>Rs {total}</h1>

                </Col>

                <Col  sm={12} md={4} className='bg-warning text-white text-center p-5'>
                  <h3>Received Fees</h3>
                  <h1>Rs {received}</h1>

                </Col>


                <Col  sm={12} md={4} className='bg-success text-white text-center p-5'>
                  <h3>Pending Fees</h3>
                  <h1>Rs {pending}</h1>

                </Col>


            </Row>
        </Container>
    )
}
