import {React, useState} from "react";
import {Container, Row,Col} from 'react-bootstrap'

import { useNavigate } from "react-router-dom";

import axios from 'axios'


export default function Signup ()
{
    const[name, setname] = useState("");

    const[email, setemail] = useState("");
    const[password, setpassword] = useState("");

    const navi = useNavigate();

    const gotosignin = () =>
    {
        navi('/')
    }

    const submitdata = (e) =>
    {
        e.preventDefault();

          if((!name) )
            {
                 alert("Name Cannnot be Empty")
            }

       else if((!email) )
            {
                 alert("Email Cannnot be Empty")
            }

            else  if((!password) )
                {
                     alert("Password Cannnot be Empty")
                }

        else
        {
              try {

                axios.post('http://localhost:3001/admin/signup', 
                    {
                        name:name,
                        email:email,
                        password:password
                    }
                  )
                  .then((res)=>
                {
                       alert(res.data.msg);
                       navi('/')

                })

              } catch (error) {

                alert("Something Wrong Went", error)
              }
        }


    }
    return(
        <>

<Container className="text-center heading">
                <Row>
                    <Col> <h1>   SignUp</h1> </Col>
                </Row>
           </Container>


           <Container>
                       <Row >

                        <Col className="bg-info loginLeft" sm={12} md={6}>

                         <h1>Welcome to SignUp</h1>
                         <h5>already have account ?</h5>
                         <button className="btn btn-warning m-3" onClick={gotosignin}>SignIn</button>

                        </Col>

                        <Col sm={12} md={6}>

                        <form onSubmit={submitdata}>

                        <h1 className="text-center m-4 bg-info p-2 text-white">Signup</h1>

                            <input type="text" placeholder="Name "  onChange={(e) => setname(e.target.value)}/>
                            <input type="email" placeholder="Email"  onChange={(e) => setemail(e.target.value)}/>
                            <input type="password" placeholder="Password"  onChange={(e) => setpassword(e.target.value)}/>
                            <input type="submit" value="Submit" />

                        </form>


                         </Col>
                        </Row>


           </Container>


        </>
    )
}
